    public boolean isConnected() {
        boolean connected = false;
        try {
            ConnectivityManager cm = (ConnectivityManager) getApplicationContext().getSystemService(Context.CONNECTIVITY_SERVICE);
            NetworkInfo nInfo = cm.getActiveNetworkInfo();
            connected = nInfo != null && nInfo.isAvailable() && nInfo.isConnected();
            return connected;
        }
        catch (Exception e) {
            Log.e("Connectivity Exception", e.getMessage());
        }
        return connected;
    }
    public long convertToMillis(int day, int month, int year) {
        Calendar calendarStart = Calendar.getInstance();
        calendarStart.set(Calendar.YEAR, year);
        calendarStart.set(Calendar.MONTH, month);
        calendarStart.set(Calendar.DAY_OF_MONTH, day);
        return calendarStart.getTimeInMillis();
    }

    public String convertDate(long mTime) {
        SimpleDateFormat df = new SimpleDateFormat("dd/MM/YYYY");
        String formattedDate = df.format(mTime);
        return formattedDate;

    }

    public String convertDate1(long mTime) {
        SimpleDateFormat df = new SimpleDateFormat("dd-MMM-YYYY");
        String formattedDate = df.format(mTime);
        return formattedDate;

    }
    private void postdatastate() {
        final ProgressDialog loading = new ProgressDialog(ViewPlannedVisits.this);
        loading.setMessage("Loading ...");
        loading.setCancelable(false);
        loading.show();


        apiInterface = APIClient.getClient().create(APIInterface.class);
        JsonObject jsonObject5=new JsonObject();
        JsonObject jsonObject6=new JsonObject();
        JsonObject jsonObject7=new JsonObject();
        JSONArray array=new JSONArray();
        jsonObject5.addProperty("axpapp", "fieldforce");
        jsonObject5.addProperty("username", uname);
        jsonObject5.addProperty("password", pswd);
        jsonObject5.addProperty("s"," ");
        jsonObject5.addProperty("sql","select a.employee,a.customername,a.corporate,a.status,a.jointcall1,a.jointcall2,a.jointcall3,b.checkintime,b.checkouttime,b.visitdetailid from visitdetail a  join checkin b on b.visitdetailid = a.visitdetailid where a.username='"+uname+"' and a.visitdate between '"+alterdates+"' and '"+alterdates1+"'");
        array.put(jsonObject5);
        try {

            jsonObject6.add("getchoices", jsonObject5);
            // array.put(jsonObject6);
            JsonArray array1=new JsonArray();
            array1.add(jsonObject6);
            jsonObject7.add("_parameters",array1);
        }
        catch (Exception e){
            e.printStackTrace();
        }

        Call<Example> call4=apiInterface.getResult(jsonObject7);
        call4.enqueue(new Callback<Example>() {

            @Override
            public void onResponse(Call<Example> call, Response<Example> response) {
                loading.dismiss();

                arraylist = new ArrayList<HashMap<String, String>>();

                try {
                    for (int i = 0; i <response.body().getResult().get(j).getResult().getRow().size(); i++) {
                        customername = response.body().getResult().get(0).getResult().getRow().get(i).getCustomername();
                        corporate=  response.body().getResult().get(0).getResult().getRow().get(i).getCorporate();
                        status = response.body().getResult().get(0).getResult().getRow().get(i).getStatus();
                        checkintime= response.body().getResult().get(0).getResult().getRow().get(i).getCheckintime();
                        checkouttime=response.body().getResult().get(0).getResult().getRow().get(i).getCheckouttime();
                        joint1=response.body().getResult().get(0).getResult().getRow().get(i).getJointcall1();
                        joint2=response.body().getResult().get(0).getResult().getRow().get(i).getJointcall2();
                        joint3=response.body().getResult().get(0).getResult().getRow().get(i).getJointcall3();
                        map = new HashMap<String, String>();
                        map.put("customername",customername);
                        map.put("corporate",corporate);
                        map.put("status",status);
                        map.put("checkintime",checkintime);
                        map.put("checkouttime",checkouttime.replace("AM"," ").replace("PM",""));
                        map.put("joint1",joint1);
                        map.put("joint2",joint2);
                        map.put("joint3",joint3);
                        arraylist.add(map); }
                    adapter = new ViewplannedAdapter(ViewPlannedVisits.this, arraylist);
                    cusappoint.setLayoutManager(new LinearLayoutManager(ViewPlannedVisits.this));
                    cusappoint.setAdapter(adapter);
                    adapter.notifyDataSetChanged();
                } catch (Exception e)
                {
                    e.printStackTrace();
                } }
            @Override
            public void onFailure(Call<Example> call, Throwable t) {

                cusappoint.setAdapter(null);
                loading.dismiss();
                Toast.makeText(ViewPlannedVisits.this, "No Records Found", Toast.LENGTH_LONG).show();
            }

        });

    }
    @Override
    public void onNetworkChanged(boolean status) {
        Log.e("MainActivity","Status: " + status);
        if(status==false)
            Toast.makeText(ViewPlannedVisits.this, "Check Internet Connection", Toast.LENGTH_LONG).show();
    }
    @Override
    protected void onPause() {
        super.onPause();
        if (networkChangeReceiver != null) {
            unregisterReceiver(networkChangeReceiver);
        }
    }
    @Override
    protected void onResume() {
        super.onResume();
        IntentFilter intentFilter = new IntentFilter(ConnectivityManager.CONNECTIVITY_ACTION);
        registerReceiver(networkChangeReceiver, intentFilter);
    }
